CREATE TABLE water_production_and_supply (
    year INT PRIMARY KEY,
    produced_water_volume INT,        -- Termelt víz mennyisége (ezer m³)
    total_supplied_water INT,         -- Összes szolgáltatott víz mennyisége (ezer m³)
    residential_consumption INT       -- Lakossági vízfogyasztás (ezer m³)
);
